import { Component, OnInit } from '@angular/core';
import { product } from './product.model';
import { HttpClient } from '@angular/common/http';
import {Tags} from "../models/tags.model";


@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent implements OnInit {

  searchQuery: string = '';
  searchUrl: string = "http://localhost:8080/search"

  sellingItems: product[] = [{
    name: 'awesome t-shirt',
    colors: ['red'],
    description: 'very very awesome t-shirt',
    image:'src',
    quantity: 10,
    sellersShop: {},
    creationDate: new Date(),
},
{
    name: 'not awesome jeans',
    colors: ['red'],
    description: 'very very not awesome jeans',
    image:'src',
    quantity: 5,
    sellersShop: {},
    creationDate: new Date(),
}];

Tags = Tags;
selectedOptionsTags:Tags[] = [];
products: product[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
  }

  search(){
    this.http.post<product[]>(this.searchUrl, this.searchQuery).subscribe(
      data => {
        console.log(data);
        this.products = data;
      },
      error => {
        console.error('Error searching products:', error);
      }
    );
  }


  toggleSelection(value: Tags) {
    const index = this.selectedOptionsTags.indexOf(value);
    if (index > -1) {
      this.selectedOptionsTags.splice(index, 1);
    } else {
      this.selectedOptionsTags.push(value);
    }
  }

}
<!-- Load icon library -->
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">

<!-- The form -->
<form class="example" action="action_page.php">
  <input type="text" placeholder="Search.." name="search" [(ngModel)]="searchQuery">
  <ng-select [multiple]="true">
      <ng-option *ngFor="let option of Tags | keyvalue" [ngValue]="option.value">
      <input type="checkbox" [checked]="selectedOptionsTags.indexOf(option.value) > -1"
             (change)="toggleSelection(option.value)"> {{ option.key }}
    </ng-option>
  </ng-select>
  <button type="submit"  (click)="search()"><i class="fa fa-search"></i></button>
</form>

<ul>
  <li *ngFor="let product of products">
    <div class="card">
      <img src="data:image/jpeg;base64," alt="Avatar" style="width:100%">
      <div class="container">
        <h4><b>{{ product.name }}</b></h4>
        <p>{{product.description}}</p>
      </div>
    </div>
  </li>
</ul>
