import { Component, OnInit, Pipe, PipeTransform, ViewEncapsulation } from '@angular/core';
import { product } from './product.model';
import { HttpClient } from '@angular/common/http';
import {Tags} from "../models/tags.model";
import { Filter } from '../models/filter';

@Pipe({name: 'enumToArray'})
export class EnumToArrayPipe implements PipeTransform {
  transform(value: any): [number, string][] {
    return Object.keys(value).filter(t => isNaN(+t)).map(t => [value[t], t]);
  }
}

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss'],
  encapsulation: ViewEncapsulation.None
})
export class SearchComponent implements OnInit {

  searchQuery: string = '';
  searchUrl: string = "http://localhost:8080/search"

colors: string[] = ["red","white", "blue", "green", "black", "yellow"];

Tags = Tags;
selectedOptionsTags:Tags[] = [];
selectedColors:string[] = [];
products: product[] = [];

constructor(private http: HttpClient) { }

  ngOnInit(): void {
  }

  search(){
    const searchFilter:Filter = {
       tags:this.selectedOptionsTags,
       wordSearch:this.searchQuery,
       colors:this.selectedColors,
       limit:10
    };
    console.log(searchFilter);
    this.http.post<product[]>(this.searchUrl, searchFilter).subscribe(
      data => {
        console.log(data);
        this.products = data;
      },
      error => {
        console.error('Error searching products:', error);
      }
    );
  }


}
<!-- Load icon library -->
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">

<!-- The form -->
<div class="header">
  <h1>UNIBUY</h1>
</div>

<form class="example" action="action_page.php">
  <input type="text" placeholder="Search.." name="search" [(ngModel)]="searchQuery">
  <br>
  <br>
  <ng-select [multiple]="true" class="custom"  placeholder="filter" [(ngModel)]="selectedOptionsTags" name="somethingf">
    <ng-option *ngFor="let option of Tags | enumToArray; let i = index" [value]="option">
    {{option}}
  </ng-option>
  </ng-select>
<br>
  <ng-select [multiple]="true" class="custom"  placeholder="color" [(ngModel)]="selectedColors" name="something">
    <ng-option *ngFor="let option of colors; let i = index" [value]="option">
      {{option}}
  </ng-option>
  </ng-select>
<br/>Selected: {{selectedColors | json}}

<br>
  <button type="submit"  (click)="search()"><i class="fa fa-search"></i></button>
</form>

<ul>
  <li *ngFor="let product of products">
    <div class="card">
      <img src="data:image/jpeg;base64," alt="Avatar" style="width:100%">
      <div class="container">
        <h4><b>{{ product.name }}</b></h4>
        <p>{{product.description}}</p>
      </div>
    </div>
  </li>
</ul>
